
package study;

public class D001 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* 숙제! for문은 이용해서 2.3.4
		 *                             5.6.7 
		 *                             8.9 
		 *라고 나올 수 있게 출력하세요!
		 *기한은 5/3까지 해보세요!                              
		 */
		// 1-100까지 정수 중에 2.3.5.7의 배수를 제외한 수를 한행에 10개씩 
		// 출력하는 프로그램을 작성하세요! for문을 하나 이용하세요!
		int a,b=1;
		for(a=1;a<=100;a++) {
			if(a==1||a%2==0||a%3==0||a%5==0||a%7==0) {continue;}
			System.out.printf("%d,",a);
		}System.out.println();
		
		int i, cnt=1;
		for (i =1;i<=100;i++) {
			if((i%2!=0)&&(i%3!=0)&&(i%5!=0)&&(i%7!=0))
			//System.out.printf("%d%c",i,(cnt%10!=0)? ' ':'\n');
			System.out.printf("%3d%c",i,(cnt++%10!=0)?' ':'\n');
			// cnt가 증가해서 10으로 나누어진다면 줄바꿈을 해준다! 그래서cnt++ 이 나오는거다!
		}
		
		
		
		
		
	}

}
